{
    {{setVar 'currentIndex' (dateFormat (now) 'i')}}
    {{setVar 'mondayFromPreviousWeek' (dateFormat (dateTimeShift date=(now) days=(subtract -6 @currentIndex)) 'yyyy-MM-dd') }}
    "previous": {
        "from": "{{ @mondayFromPreviousWeek }}",
        "to": "{{ dateFormat (dateTimeShift date=@mondayFromPreviousWeek days=7) 'yyyy-MM-dd' }}",
        "timeline": [
          {{#repeat 7}}
            {{setVar 'timelineDay' (dateTimeShift date=@mondayFromPreviousWeek days=@index) }}
            {
              "date": "{{ dateFormat @timelineDay 'yyyy-MM-dd' }}",
              {{#if (includes '135' (dateFormat @timelineDay 'i') )}}
              "value": {{ faker 'datatype.number' min=10 max=20 }}
              {{/if}}
              {{#if (includes '24' (dateFormat @timelineDay 'i') )}}
              "value": {{ faker 'datatype.number' min=15 max=32 }}
              {{/if}}
              {{#if (includes '67' (dateFormat @timelineDay 'i') )}}
              "value": {{ faker 'datatype.number' min=0 max=6 }}
              {{/if}}
            }
          {{/repeat}}
        ]
    },
    {{setVar 'mondayFromThisWeek' (dateFormat (dateTimeShift date=(now) days=(subtract 1 @currentIndex)) 'yyyy-MM-dd') }}
    "current": {
        "from": "{{ @mondayFromThisWeek }}",
        "to": "{{ dateFormat (now) 'yyyy-MM-dd' }}",
        "timeline": [
          {{#repeat @currentIndex}}
            {{setVar 'timelineDay' (dateTimeShift date=@mondayFromThisWeek days=@index) }}
            {
              "date": "{{ dateFormat @timelineDay 'yyyy-MM-dd' }}",
              {{#if (includes '135' (dateFormat @timelineDay 'i') )}}
              "value": {{ faker 'datatype.number' min=10 max=20 }}
              {{/if}}
              {{#if (includes '24' (dateFormat @timelineDay 'i') )}}
              "value": {{ faker 'datatype.number' min=15 max=32 }}
              {{/if}}
              {{#if (includes '67' (dateFormat @timelineDay 'i') )}}
              "value": {{ faker 'datatype.number' min=0 max=6 }}
              {{/if}}
            }
          {{/repeat}}
        ]
    }
}

{
    "previous": {
        {{setVar 'dayFromPreviousWeek' (dateFormat (dateTimeShift date=(now) days=-7) 'yyyy-MM-dd') }}
        "from": "{{ dateTimeShift date=@dayFromPreviousWeek hours=6 }}",
        "to": "{{ dateTimeShift date=@dayFromPreviousWeek hours=21 }}",
        "timeline": [
          {{#repeat 15}}
            {{setVar 'timelineHour' (dateTimeShift date=(dateFormat @dayFromPreviousWeek 'yyyy-MM-dd') hours=(add 7 @index)) }}
            {
              "date": "{{ @timelineHour }}",
              "value": {{ faker 'datatype.number' min=0 max=28 }}
            }
          {{/repeat}}
        ]
    },
    "current": {
        {{setVar 'today' (dateFormat (now) 'yyyy-MM-dd') }}
        "from": "{{ dateTimeShift date=@today hours=6 }}",
        "to": "{{ now }}",
        "timeline": [
          {{#repeat (subtract (dateFormat (now) 'HH') 7) }}
            {{setVar 'timelineHour' (dateTimeShift date=@today hours=(add @index 7)) }}
            {
              "date": "{{ @timelineHour }}",
              "value": {{ faker 'datatype.number' min=10 max=28 }}
            }
          {{/repeat}}
        ]
    }
}

{
  {{setVar 'subscriptionStartDate' (date (dateTimeShift date=(now) months=-1) (now) 'yyyy-MM-dd') }}
    "id": {{ faker 'datatype.number' min=1 max=800 }},
